function Thermostat(){this.temperature=20,this.powerSavingMode="on",this.powerLevel="medium"}Thermostat.prototype.up=function(){if(!("off"===this.powerSavingMode&&this.temperature<32||"on"===this.powerSavingMode&&this.temperature<25))throw new Error("Max temperature reached");this.temperature+=1,this.powerLevelUpdate()},Thermostat.prototype.down=function(){if(!(this.temperature>10))throw new Error("Temp too low");this.temperature-=1,this.powerLevelUpdate()},Thermostat.prototype.powerSaveOn=function(){this.temperature=25,this.powerLevelUpdate(),this.powerSavingMode="on"},Thermostat.prototype.powerSaveOff=function(){this.powerSavingMode="off"},Thermostat.prototype.reset=function(){this.temperature=20,this.powerLevelUpdate()},Thermostat.prototype.powerLevelUpdate=function(){this.temperature<18?this.powerLevel="low":this.temperature>=18&&this.temperature<25?this.powerLevel="medium":this.powerLevel="high"};