function Thermostat(){this.DEFAULT_TEMP=20,this.MAX_TEMP=32,this.MIN_TEMP=10,this.PS_MAX_TEMP=25,this.powerSavingMode="on",this.powerLevel="medium",this.temperature=this.DEFAULT_TEMP}Thermostat.prototype.up=function(){if(!("off"===this.powerSavingMode&&this.temperature<this.MAX_TEMP||"on"===this.powerSavingMode&&this.temperature<this.PS_MAX_TEMP))throw new Error("Max temperature reached");this.temperature+=1,this.powerLevelUpdate()},Thermostat.prototype.down=function(){if(!(this.temperature>this.MIN_TEMP))throw new Error("Temp too low");this.temperature-=1,this.powerLevelUpdate()},Thermostat.prototype.powerSaveOn=function(){this.temperature>this.PS_MAX_TEMP&&(this.temperature=this.PS_MAX_TEMP,this.powerLevelUpdate()),this.powerSavingMode="on"},Thermostat.prototype.powerSaveOff=function(){this.powerSavingMode="off"},Thermostat.prototype.reset=function(){this.temperature=this.DEFAULT_TEMP,this.powerLevelUpdate()},Thermostat.prototype.powerLevelUpdate=function(){this.temperature<18?this.powerLevel="low":this.temperature>=18&&this.temperature<25?this.powerLevel="medium":this.powerLevel="high"};