$(document).ready(function(){$("#select-city").submit(function(e){console.log("clicked"),e.preventDefault();var t="http://api.openweathermap.org/data/2.5/weather?q=",n=$("#current-city").val(),c="&APPID=e4513b76de988bd77bb49e09c03c1ca6",o="&units=metric";$.get(t+n+c+o,function(e){console.log(e),$("#conditions").html(e.name+" | "+Math.round(e.main.temp)+"&#176;C | "+e.weather[0].description)})})});